import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Movie.css";

const Movie = () => {
  const [Input, setInput] = useState("");   // user input
  const [Data, setData] = useState([]);     // api data
  const [final, setFinal] = useState('');   //user data store

  const ApiFetch = async () => {
    setIsLoading(true); // Set loading to true when the fetch starts

    const url = `https://online-movie-database.p.rapidapi.com/auto-complete?q=${final}`;
    const options = {
      method: 'GET',
      headers: {
        'X-RapidAPI-Key': '9fe50803f8msh28e820e949a40f3p1cff8ajsnde2751a2774b',
        'X-RapidAPI-Host': 'online-movie-database.p.rapidapi.com'
      }
    };

    try {
      const response = await fetch(url, options);
      const result = await response.json();
      setData(result.d);
      console.log(result);
    } catch (error) {
      console.error(error);
    } finally {
      setIsLoading(false); // Set loading to false when the fetch completes (success or error)
    }
  };

  useEffect(() => {
    ApiFetch()
  }, [final]);

  const FormSubmit = e => {
    e.preventDefault();
    setFinal(Input);
  }

  return (
    <>
      <div className="container">
        <form onSubmit={FormSubmit} className="input-box">
          <input
            type="text"
            id="input-item"
            value={Input}
            onChange={(e) => {
              setInput(e.target.value);
            }}
          />
          <button type="submit">Search</button>
        </form>
        <div className="search-box">
          {isLoading ? ( // Render loader when isLoading is true
            <div className="loader">Loading...</div>
          ) : (
            Data.map((val, idx) => {
              return (
                <div className="card" style={{ height: '400px' }} key={idx}>
                  {val.i && val.i.imageUrl && (
                    <img
                      src={val.i.imageUrl}
                      alt="Photos"
                      style={{ height: "3rem", width: "3.2rem" }}
                    />
                  )}
                  <p className="name">{val.l}</p>
                  <p className="year">{val.s}</p>
                </div>
              );
            })
          )}
        </div>
      </div>
    </>
  );

};

export default Movie;
