import React, { useState } from "react";
import axios from "axios";
import "./Movie.css";

const Movie = () => {
  const [Input, setInput] = useState("");   //user input
  const [Data, setData] = useState([]);
  const [Final, setFinal] = useState('');

  const ApiFetch = async () => {
    const options = {
      method: "GET",
      url: `https://imdb-movies-web-series-etc-search.p.rapidapi.com/${Input}.json`,
      headers: {
        "X-RapidAPI-Key": "9fe50803f8msh28e820e949a40f3p1cff8ajsnde2751a2774b",
        "X-RapidAPI-Host": "imdb-movies-web-series-etc-search.p.rapidapi.com",
      },
    };

    try {
      const response = await axios.request(options);
      setData(response.data)
    } catch (error) {
      console.error(error);
    }
  };
  const FormSubmit = e => {
    e.preventDefault();
    setFinal(Input);
  }
  return (
    <>
      <div className="container">
        <form onSubmit={FormSubmit} className="input-box">
          <input type="text" id="input-item" value={Input} onChange={(e) => {
            setInput(e.target.value);
          }} />
          <button type="submit">Search</button>
        </form>
        <div className="search-box">
          <div className="card">
            <img src="" alt="" />
            <p className="name"></p>
            <p className="year"></p>
            <p className="producer"></p>
            <p className="rank"></p>
          </div>
        </div>
      </div>
    </>
  );
};

export default Movie;
