import React, { useState, useEffect } from "react";
// import axios from "axios";
import "./Movie.css";

const Movie = () => {
  const [Input, setInput] = useState("");   // user input
  const [Data, setData] = useState([]);     // api data
  const [Final, setFinal] = useState('');   //user data store

  useEffect((e) => {
    apif()
  }, [Final])

  const apif = async () => {
    const url = 'https://imdb-movies-web-series-etc-search.p.rapidapi.com/thegodfather.json';
    const options = {
      method: 'GET',
      headers: {
        'X-RapidAPI-Key': '9fe50803f8msh28e820e949a40f3p1cff8ajsnde2751a2774b',
        'X-RapidAPI-Host': 'imdb-movies-web-series-etc-search.p.rapidapi.com'
      }
    };

    try {
      const response = await fetch(url, options);
      const result = await response.json();
      setData(result.d)
      console.log(result);
    } catch (error) {
      console.error(error);
    }
  };
  const FormSubmit = e => {
    e.preventDefault();
    setFinal(Input);
  }

  return (
    <>
      <div className="container">
        <form onSubmit={FormSubmit} className="input-box">
          <input type="text" id="input-item" value={Input} onChange={(e) => {
            setInput(e.target.value);
          }} />
          <button type="submit">Search</button>
        </form>
        <div className="search-box">
          {Data.map((val) => {
            return (
              <div className="card">
                <img src={val.i.imageURL} alt="Photos" style={{ height: "3rem", width: "3.2rem" }} />
                <p className="name">{val.l}</p>
                <p className="year">{val.y}</p>
                <p className="producer">{val.s}</p>
                <p className="rank">{val.rank}</p>
              </div>
            )
          })}
        </div>
      </div>
    </>
  );
};

export default Movie;
